import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const translations = {
  en: {
    ibanLabel: 'IBAN:',
    submitButton: 'Submit',
    resetButton: 'Reset',
    ibanCorrect: 'Your IBAN is correct:',
    bankNotFound: 'Bank not found',
  },
  ar: {
    ibanLabel: 'الرقم الدولي للحساب المصرفي:',
    submitButton: 'تأكيد',
    resetButton: 'إعادة تعيين',
    ibanCorrect: 'الرقم الدولي للحساب المصرفي صحيح:',
    bankNotFound: 'البنك غير موجود',
  },
};

export default function Iban() {
  const [language, setLanguage] = useState('en');
  const [iban, setIban] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const validateIBAN = (iban) => {
    if (!iban) return translations[language].ibanRequired;

    iban = iban.trim();

    if (iban.length !== 24) return translations[language].ibanLengthError;
    if (!iban.startsWith('SA')) return translations[language].ibanStartError;

    return '';
  };

  const getBankByIban = (event) => {
    event.preventDefault();
    const errorMessage = validateIBAN(iban);
    if (errorMessage) {
      setError(errorMessage);
      setMessage('');
    } else {
      const BankCode = iban.substring(4, 6);
      const bank = banks.find((b) => b.samaCode === BankCode);
      if (bank) {
        setMessage(
          <div>
            <p className='fs-4'>{translations[language].ibanCorrect}: {bank.nameAr}</p>
            <img src={bank.img} alt={bank.nameEn} style={{ width: '250px', height: 'auto' }} />
          </div>
        );
        setError('');
      } else {
        setError(translations[language].bankNotFound);
        setMessage('');
      }
    }
  };

  const formatIBAN = (value) => {
    return value.replace(/\s/g, '').replace(/(.{4})/g, '$1 ').trim();
  };

  return (
    <>
      <div className="center">
        <select onChange={(e) => setLanguage(e.target.value)}>
          <option value="en">English</option>
          <option value="ar">Arabic</option>
        </select>

        <div className="container mt-5">
          <form onSubmit={getBankByIban}>
            <label htmlFor="iban" className="badge bg-success fs-6 mb-2">
              {translations[language].ibanLabel}
            </label>
            {error && <h4 className="text-danger">{error}</h4>}
            <input
              type="text"
              name="iban"
              id="iban"
              className={`form-control border-4 ${error ? 'border-danger' : 'border-secondary'}`}
              value={formatIBAN(iban)}
              onChange={(e) => setIban(e.target.value)}
              placeholder={translations[language].ibanPlaceholder}
            />
            <div className="form-group">
              <button type="submit" className="btn btn-primary mt-2">
                {translations[language].submitButton}
              </button>
              <button
                type="reset"
                className="btn btn-danger mt-2 mx-3"
                onClick={() => {
                  setIban('');
                  setError('');
                  setMessage('');
                }}
              >
                {translations[language].resetButton}
              </button>
            </div>
          </form>
          <div id="result" className="mt-3">
            {message}
          </div>
        </div>
      </div>
    </>
  );
}
